package com.ConditionalStatement;

public class NestedIfCondition {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
     int a=10;
     int b=20;
     int c=30;
    if(a>b)
     {
	   System.out.println("a is greater");	
     }
   else
	 if(b>c)
       {
	    System.out.println("b is greater");
        }
	   else
	   {
        System.out.println("a is greater");
	   }

  }
	
}
